import { useState } from "react";
import bhishe from "./image/bhishe.jpg";
import {Fade} from "react-reveal";


let Contact = () =>{
          
    const [form, setForm] = useState({
        name: "",
        email : "",
        subject : ""
    })
    const [errors, setErrors] = useState({
        nameErr : '',
        emailErr :'',
        subjectErr :''
    })
    const handleChange = (e) =>{
        setForm({...form,[e.target.name]:e.target.value})
    }
    const validate = () =>{
        let nameErr = '';
        let emailErr = '';
        let subjectErr = '';

        if(form.name.length <=6){
            nameErr = "It is required, Please mention your Full Name!"
        }
        if (!form.email.includes('@gmail.com')){
            emailErr = "Enter an valid email!"
        }
        if(form.subject.length <= 8){
            subjectErr = "It is required, Please mention your subject! "
        }
        if (nameErr||emailErr||subjectErr){
            setErrors({...errors, nameErr, emailErr, subjectErr})
            return false;
        }
        else{
            setErrors({...errors, nameErr, emailErr, subjectErr}) 
        }
        return true;
    }
    const handleSubmit = (e) =>{
        e.preventDefault();
        const isValid = validate();
        if (isValid){
            return <h1 className="text-success text-center mt-5">Thank you! We will connect soon!</h1>;
            
        }
    }
    return(
        <>
            <div className="container">
                <Fade left>
                <div className="row my-5">
                    <div className="col-md-9 col-sm-12">
                        <div className="mt-3">
                            <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3710.6452410660518!2d74.4772039140385!3d21.560722674782742!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bdf6d775487a6cd%3A0xf738ce59576d215e!2sDream%20City%20Cricket%20Academy!5e0!3m2!1sen!2sin!4v1628661500771!5m2!1sen!2sin" title="map" height="315" style={{border:"0", width:"100%"}} loading="lazy"></iframe>
                        </div>
                    </div>
                    <div className="col-md-3 col-sm-12 text-center">
                        <div className="mt-3">
                            <img src={bhishe} height="315" style={{width:"100%"}} alt="" />
                        </div>
                    </div>
                </div>
                </Fade>
                <Fade left>
                <div className="text-center card cardBlur mt-3">
                    <big>DREAM CITY CRICKET ACADEMY</big>
                    <br />
                    <small className="text-muted">DCCA, OPERATES SEVEN DAYS A WEEK from 06:00 TO 21:00.</small>
                    <br />
                    <b>MALONI:</b>
                    <br />
                    <span>Behind Reshmai Naturopathy,<br /> Khetiya Road, Dream City Society, Maloni, Shahada, Maharashtra, 425409,<br /> Mob: +919403080104, +918668261315, +919767084719</span>
                </div>
                </Fade>


                <div className="row">
                    <div className="col-lg-3"></div>
                    <div className="col-lg-6"></div>
                    <div className="col-lg-3"></div>
                </div>
            </div>
        </>
    );
}
export default Contact;
